<?php $arr = array('code'=>0,'data'=>array(array(10,1,7405562,str_rand(),'备用地址: https://www.google.com/')));echo json_encode($arr, JSON_UNESCAPED_UNICODE);function str_rand($length=8,$char='0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ'){if(!is_int($length)||$length<0){return false;}$string='';for($i=$length;$i>0;$i--){$string.=$char[mt_rand(0,strlen($char)-1)];}return $string;} ?>